const GOV_BANNER=document.querySelector("#gov-banner"),GOV_BANNER_EXPAND=document.querySelector(".gov-banner-expand");function toggleGovBanner(e){e?(document.querySelector(".gov-banner-panel").classList.remove("active"),document.querySelector(".gov-banner-expand").classList.remove("active")):(document.querySelector(".gov-banner-panel").classList.add("active"),document.querySelector(".gov-banner-expand").classList.add("active"))}let banner_scroll_px,scroll_banner_threshold_px;function createScrollListener(){return banner_scroll_px=.15*document.body.clientHeight,scroll_banner_threshold_px=banner_scroll_px<25?25:banner_scroll_px,document.addEventListener("scroll",(()=>{document.body.scrollTop>scroll_banner_threshold_px||document.documentElement.scrollTop>scroll_banner_threshold_px?(toggleGovBanner(!0),GOV_BANNER.classList.add("slide-out")):GOV_BANNER.classList.remove("slide-out")}))}let govBannerScrollEvent=createScrollListener();GOV_BANNER.addEventListener("click",(e=>{e.preventDefault(),removeEventListener("scroll",govBannerScrollEvent),toggleGovBanner(GOV_BANNER_EXPAND.classList.contains("active")),govBannerScrollEvent=createScrollListener()})),GOV_BANNER_EXPAND.addEventListener("keypress",(e=>{"Enter"===e.key&&e.target.click()})),window.addEventListener("load",(()=>{GOV_BANNER.classList.remove("hide")}));